mapper.select:
SELECT arnumber, arcontent, arrate, ardate, ar_anumber, ar_mnumber FROM agent_review WHERE ar_anumber = #{anumber}
ORDER BY arrate ASC
ORDER BY arrate DESC
ORDER BY arnumber DESC
ORDER BY arnumber ASC
LIMIT #{pager.limit} OFFSET #{pager.offset}

select count(*) from
agent_review
where ar_anumber = #{anumber}

mapper.delete:
delete from
agent_review
where arnumber=#{arnumber}
and ar_anumber = #{anumber}
and
ar_mnumber = #{userNumber}

