mapper.update:
	      update property set pdeposite=#{pdeposite}, prentalfee=#{prentalfee}, pfloortype=#{pfloortype}, pfloor=#{pfloor}, 
	      					psize=#{psize}, pmaintenance=#{pmaintenance}, ptitle=#{ptitle}, pcategory=#{pcategory},
	      					paddress=#{paddress}, paddressdetail=#{paddressdetail}, ppostcode=#{ppostcode}, 
	      					pstatus=#{pstatus} , platitude=#{platitude}, plongitude=#{plongitude}

	      , pthumbnailoname=#{pthumbnailoname}, pthumbnailtype=#{pthumbnailtype}, 
	      pthumbnaildata=#{pthumbnaildata}

      where pnumber=#{pnumber}

	update property 
	set pstatus=#{pstatus}
   	where pnumber=#{pnumber}


update property 
set phitcount=phitcount+1 
where pnumber=#{pnumber}

mapper.select:
      select 
      	count(*) 
      from property

	      pstatus='활성화'

      		and pcategory=#{rentType}


      		and (
                paddress like '%' || #{keyword} || '%' 
                or paddressdetail like '%' || #{keyword} || '%'
            )


      	and PLATITUDE = #{platitude}
      	and plongitude = #{plongitude}


select *
from (
    select p.*, row_number() over (order by p.pnumber desc) as rnum
    from property p
    where pstatus='활성화'
)
where rnum between #{offset} + 1 and #{offset} + #{limit}


    select *
    from (
        select p.*, row_number() over (order by p.pnumber desc) as rnum
        from property p 


        	pstatus='활성화'

	      		and pcategory=#{rentType}


	      		and (
	                p.paddress like '%' || #{keyword} || '%' 
	                or p.paddressdetail like '%' || #{keyword} || '%'
	            )


	      		and PLATITUDE = #{platitude}
	      		and PLONGITUDE = #{plongitude}


        order by

				pdeposite asc


				pdeposite desc


			,



				pdate asc


				pdate desc


				pnumber desc

 )
    where rnum between #{offset} + 1 and #{offset} + #{limit}


select rnum, pnumber,pdeposite,prentalfee, pfloortype, pfloor, psize, pmaintenance, pdate, pstatus, ptitle, pcategory, p_unumber, pthumbnaildata, pthumbnailoname, pthumbnailtype
from (
		select rownum as rnum, pnumber,pdeposite,prentalfee, pfloortype, pfloor, psize, pmaintenance, pdate, pstatus, ptitle, pcategory, p_unumber, pthumbnaildata, pthumbnailoname, pthumbnailtype
from(
	select pnumber,pdeposite,prentalfee, pfloortype, pfloor, psize, pmaintenance, pdate, pstatus, ptitle, pcategory, p_unumber, pthumbnaildata, pthumbnailoname, pthumbnailtype
	from property
	where p_unumber=#{unumber}
	order by pnumber desc
)
where rownum <= #{pager.endRowNo}
)
where rnum >= #{pager.startRowNo}

select count(*) from property
where pstatus='거래완료'
and p_unumber = #{userNumber}
