mapper.delete:
delete
from user_comment
where ucnumber=#{cnumber} and uc_pnumber = #{pnumber}
and uc_unumber = #{userNumber}

mapper.update:
update user_comment
set uccomment=#{uccomment}, ucremoved=#{ucremoved}
where ucnumber=#{ucnumber}

mapper.select:
select count(*)
from user_comment
where uc_pnumber=#{pnumber}

WITH RECURSIVE comment_tree AS ( SELECT ucnumber, uccomment, ucdate, uc_unumber, ucparentnumber, uc_pnumber, ucremoved, ROW_NUMBER() OVER ( ORDER BY
CASE WHEN ucparentnumber = 0 THEN ucnumber ELSE NULL END DESC, CASE WHEN ucparentnumber = 0 THEN NULL ELSE ucnumber END ASC
ucnumber ASC
) AS rnum FROM user_comment WHERE uc_pnumber = #{pnumber} UNION ALL SELECT c.ucnumber, c.uccomment, c.ucdate, c.uc_unumber, c.ucparentnumber, c.uc_pnumber, c.ucremoved, t.rnum FROM user_comment c INNER JOIN comment_tree t ON c.ucparentnumber = t.ucnumber ) SELECT * FROM comment_tree ORDER BY rnum LIMIT #{pager.limit} OFFSET #{pager.offset}
